                    if (data.success) {
                        // Show success message
                        successAlert.classList.remove('d-none');

                        // Update cart count in navbar
                        const cartCount = document.querySelector('.cart-count');
                        if (cartCount) {
                            cartCount.textContent = data.cart_items_count;
